    private void btnPokreniServerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPokreniServerActionPerformed
        if (!aktivan) {
            pokreniServer();
        } else {
            zaustaviServer();
        }
    }//GEN-LAST:event_btnPokreniServerActionPerformed

