    private void pokreniServer() {
        psn = new PokretanjeServeraNit();
        psn.start();
        labelaPokrenut.setText("Server je pokrenut.");
        btnPokreniServer.setText("Zaustavi server");
        aktivan = true;
    }

    private void zaustaviServer() {
        psn.zaustaviNiti();
        labelaPokrenut.setText("Server nije pokrenut.");
        btnPokreniServer.setText("Pokreni server");
        aktivan = false;
    }
    
    private void postaviNaslovForme(){
        this.setTitle("Server forma");
    }
